<?php

namespace AdminBundle\Repository;

use AdminBundle\Entity\PersonnalityType;
use AdminBundle\Entity\Question;
use AdminBundle\Entity\Response;

/**
 * RESPONSERepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ResponseRepository extends \Doctrine\ORM\EntityRepository
{
    /**
     * Renvois toutes les réponses enregistrer en base
     *
     * @return array
     */
    public function getResponses()
    {
        return $this->findAll();
    }

    /**
     * Renvois la réponse correspondant à un id
     *
     * @param $id
     *
     * @return null|object
     */
    public function getResponseById($id)
    {
        return $this->findOneBy(['id' => $id]);
    }

    /**
     * Renvois les réponses pour une question
     *
     * @param $questionId
     * @return array|bool
     */
    public function getResponseByQuestionId($questionId)
    {
        $question = $this->getEntityManager()->getRepository("AdminBundle:Question")->getQuestionById($questionId);

        return $this->findBy(['question' => $question]);
    }

    /**
     * Créer une réponse
     *
     * @param $label
     * @param $value
     * @param $image
     * @param Question $question
     * @param PersonnalityType $personnalityType
     *
     * @return Response|bool
     */
    public function postResponse($label, $value, $image, Question $question, PersonnalityType $personnalityType)
    {
        if (!$this->checkIfResponseAlreadyExist($value, $question, $personnalityType) && $value != 50)
        {
            $em = $this->getEntityManager();

            $response = new Response();

            $response->setLabel($label)
                ->setValue($value)
                ->setImage($image)
                ->setQuestion($question)
                ->setPersonnalityType($personnalityType);

            $em->persist($response);
            $em->flush();

            return $response;
        }

        return false;
    }

    /**
     * Modifie une réponse
     *
     * @param $id
     * @param $label
     * @param $value
     * @param $image
     * @param PersonnalityType $personnalityType
     *
     * @return bool|object
     */
    public function putResponse($id, $label, $value, $image, PersonnalityType $personnalityType)
    {
        $em = $this->getEntityManager();
        if($value != 50)
        {
            $response = $this->getResponseById($id);

            if (!$this->checkIfResponseAlreadyExist($value, $response->getQuestion(), $personnalityType, $response->getId()))
            {
                $response->setLabel($label)
                    ->setValue($value)
                    ->setImage($image)
                    ->setPersonnalityType($personnalityType);

                $em->persist($response);
                $em->flush();

                return $response;
            }
        }
        return false;
    }

    /**
     * Supprime une réponse
     * @param $id
     */
    public function deleteResponse($id)
    {
        $em = $this->getEntityManager();

        $response = $this->getResponseById($id);

        $em->remove($response);
        $em->flush();
    }

    /**
     * Renvois true si la réponse existe déjà
     *
     * @param $label
     *
     * @return bool
     */
    public function checkIfResponseAlreadyExist($value, Question $question, PersonnalityType $personnalityType, $id = null)
    {
        if($value > 50)
        {
            $this->checkerProcess("r.value > 50", $question, $personnalityType, $id);
        }
        else if($value < 50)
        {
            $this->checkerProcess("r.value < 50", $question, $personnalityType, $id);
        }
        return false;
    }

    private function checkerProcess($string, $question, $personnalityType, $id)
    {
        $responses = $this->getEntityManager()->createQueryBuilder()
            ->select("r")
            ->from("AdminBundle:Response", "r")
            ->where("r.question = :question")
            ->andWhere("r.personnalityType = :personnalityType")
            ->andWhere($string)
            ->setParameters([
                ":question" => $question,
                ":personnalityType" => $personnalityType,
            ])
            ->getQuery()
            ->getResult();

        if (count($responses))
        {
            if(is_null($id))
            {
                return true;
            }
            else
            {
                foreach ($responses as $response)
                {
                    if($response->getId() === $id)
                    {
                        return false;
                    }
                }

                return true;
            }
        }
    }
}
