$(document).ready(function(){$("#modalConfirm").on("show.bs.modal",function(event){var button=$(event.relatedTarget);var id=button.data("id");var name=button.data("name");var modal=$(this);var href=button.data("href");$("#confirm").attr("href",href);modal.find(".modal-title").text("Confirmation de la suppression de "+name);modal.find(".modal-body p").html("Etes-vous sûr de supprimer le tempérament :&nbsp;<strong>"+name+"</strong>")});$(".updateButton").each(function(){$(this).unbind("click").bind("click",function(){var idTemperament=$(this).attr("data-id");var name=$("#nameUpdate"+idTemperament).val();var temperament=$("#temperamentUpdate"+idTemperament).val();var opposedTemperament=$("#opposedTemperamentUpdate"+idTemperament).val();$.ajax({url:"/tdb-admin/temperament/put/"+idTemperament,type:"PUT",datatype:"json",data:{name:name,temperament:temperament,opposedTemperament:opposedTemperament},success:function(result){$("#temperamentTitle"+idTemperament).html(result.name);$("#temperament"+idTemperament).html(result.temperament);$("#opposedTemperament"+idTemperament).html(result.opposedTemperament);$("#modalUpdateTemperament"+idTemperament).modal("hide");$("body").removeClass("modal-open");$(".modal-backdrop").remove();$("#responseMessageContent").fadeIn(250).removeClass("hidden");$("#responseMessage").html(result.message);setTimeout(function(){$("#responseMessageContent").fadeOut(250)},5e3)}})})})});